<?php
// Assumes col_width, num_cols, and gutter_width are set.
// Cannot handle more than one grid yet. Use ids to sort that out.
?>
/* Generated By Scarlet's Grid Library - Based on the 1Kb Grid */

<?php for ($i = 1, $width=$col_width; $i <= $num_cols; $i++, $width+=$col_width+$gutter_width): ?>
.grid.col_<?php echo $i; ?> { width:<?php echo $width; ?>px; }
<?php endfor ?>

.col {
	margin: 0 <?php echo $gutter_width/2; ?>px;
	overflow: hidden;
	float: left;
	display: inline;
}
.row {
	width: <?php echo ($col_width+$gutter_width)*$num_cols; ?>px;
	margin: 0 auto;
	overflow: hidden;
}
.row .row {
	margin: 0 -<?php echo $gutter_width/2; ?>px;
	width: auto;
	display: inline-block;
}