<?php
// Assumes col_width, num_cols, and gutter_width are set.
// Cannot handle more than one grid yet. Use ids to sort that out.
?>
/* Generated By Scarlet's Grid Library - Based on the 1Kb Grid */

<?php for ($i = 1, $width=$colWidth; $i <= $numCols; $i++, $width+=$colWidth+$gutterWidth): ?>
.scarlet-grid-col.col-<?php echo $i; ?> { width:<?php echo $width; ?>px; }
<?php endfor ?>

.scarlet-grid-col {
	margin: 0 <?php echo $gutterWidth/2; ?>px;
/*	overflow: hidden;*/
	float: left;
	display: inline;
}
.scarlet-grid-row {
	width: <?php echo ($colWidth+$gutterWidth)*$numCols; ?>px;
	margin: 0 auto;
/*	overflow: auto;*/
}
.scarlet-grid-row .scarlet-grid-row {
	margin: 0 -<?php echo $gutterWidth/2; ?>px;
	width: auto;
	display: inline-block;
}